$(document).ready(function(){
    var current_scrollY;
    $(".mordalwindow").colorbox({
      iframe:true, width:"1110px", height:"85%", fixed:true, textAlign:"center",
      onOpen:function(){ current_scrollY = $( window ).scrollTop();

      $( '#wrapper' ).css( {
        position: 'fixed',
        width: '100%',
        top: -1 * current_scrollY
      } ); },
      onClosed:function(){ $( '#wrapper' ).attr( { style: 'background: #e0e0db' } );$( 'html, body' ).prop( { scrollTop: current_scrollY } ); }
    });
  });